mapper.select:
SELECT T101.OBJ_ID AS OBJ_ID ,T101.ATR_ID AS ATR_ID ,T101.ATR_ID_SEQ AS ATR_ID_SEQ ,T101.ATR_VAL_SEQ AS ATR_VAL_SEQ ,T101.OBJ_ATR_VAL AS OBJ_ATR_VAL ,T101.CRET_DT AS CRET_DT ,T101.CRETR_ID AS CRETR_ID ,${objAtrValSelectStatment} AS EXTEND_OBJ_ATR_VAL FROM ( SELECT T102.OBJ_ID AS OBJ_ID ,T101.ATR_ID AS ATR_ID ,T101.ATR_ID_SEQ AS ATR_ID_SEQ ,T107.ATR_VAL_SEQ AS ATR_VAL_SEQ ,CASE TO_CHAR(T101.ATR_ID_SEQ) WHEN '1' THEN TO_CHAR(T102.OBJ_TYPE_ID) WHEN '2' THEN TO_CHAR(T102.OBJ_ID) WHEN '3' THEN TO_CHAR(T102.ADM_OBJ_ID) WHEN '4' THEN TO_CHAR(T102.OBJ_NM) WHEN '5' THEN TO_CHAR(T102.OBJ_ABBR_NM) WHEN '6' THEN TO_CHAR(T102.OBJ_DESC) WHEN '7' THEN TO_CHAR(T102.PATH_OBJ_TYPE_ID) WHEN '8' THEN TO_CHAR(T102.PATH_OBJ_ID) WHEN '10' THEN TO_CHAR(T102.DEL_YN) WHEN '11' THEN TO_CHAR(T102.CRET_DT, 'YYYY-MM-DD HH24:MI:SS') WHEN '12' THEN TO_CHAR(T102.CRETR_ID) WHEN '13' THEN TO_CHAR(T102.CHG_DT, 'YYYY-MM-DD HH24:MI:SS') WHEN '14' THEN TO_CHAR(T102.CHGR_ID) ELSE TO_CHAR(T107.OBJ_ATR_VAL) END AS OBJ_ATR_VAL ,T102.CRET_DT ,T102.CRETR_ID ,T101.SORT_NO FROM PEN_OBJ_M T102 INNER JOIN PEN_OBJ_TYPE_ATR_D T101 ON T101.OBJ_TYPE_ID = T102.OBJ_TYPE_ID LEFT OUTER JOIN PEN_OBJ_D T107 ON T107.OBJ_TYPE_ID = T102.OBJ_TYPE_ID AND T107.OBJ_ID = T102.OBJ_ID AND T107.ATR_ID_SEQ = T101.ATR_ID_SEQ
T102.OBJ_TYPE_ID = #{objTypeId} AND T102.OBJ_ID = #{objId}
) T101 ${objAtrValJoinStatment} ORDER BY T101.SORT_NO, T101.ATR_VAL_SEQ

SELECT T101.*
FROM PEN_OBJ_M T101
WHERE T101.OBJ_TYPE_ID = #{objTypeId}

  AND T101.OBJ_ID = #{objId}


  AND T101.PATH_OBJ_TYPE_ID = #{pathObjTypeId}


  AND T101.PATH_OBJ_ID = #{pathObjId}

SELECT CRET_DT, CRETR_ID
FROM PEN_OBJ_M
WHERE OBJ_TYPE_ID = #{objTypeId}
AND OBJ_ID = #{objId}

SELECT CRET_DT, CRETR_ID
FROM PEN_OBJ_M_T
WHERE OBJ_TYPE_ID = #{objTypeId}
AND OBJ_ID = #{objId}
AND APRV_ID = #{aprvId}

SELECT CRET_DT, CRETR_ID
FROM PEN_OBJ_D
WHERE OBJ_TYPE_ID = #{objTypeId}
AND OBJ_ID = #{objId}
AND ROWNUM = 1

SELECT *
FROM PEN_REL_TYPE_M


        AND REL_TYPE_ID = #{relTypeId} 


        AND OBJ_TYPE_ID = #{objTypeId} 


        AND REL_OBJ_TYPE_ID = #{relObjTypeId} 


        AND META_REL_CD = #{relTypeCd}


        AND META_REL_CD = #{metaRelCd}


AND DEL_YN = 'N'

SELECT *
FROM PEN_OBJ_R
WHERE OBJ_TYPE_ID = #{objTypeId}
AND OBJ_ID = #{objId}
AND REL_OBJ_TYPE_ID = #{relObjTypeId}

    AND REL_TYPE_ID = #{relTypeId}

SELECT *
FROM PEN_OBJ_R
WHERE OBJ_TYPE_ID = #{objTypeId}
AND OBJ_ID= #{objId}
AND DEL_YN = 'N'
AND REL_TYPE_ID IN (
    SELECT REL_TYPE_ID
    FROM PEN_REL_TYPE_M
    WHERE OBJ_TYPE_ID =#{objTypeId}
    AND META_REL_CD = 'IR'
    AND DEL_YN = 'N'
)

        SELECT DISTINCT T102.OBJ_TYPE_ID
             , '['||T103.OBJ_TYPE_NM||'] '||T102.OBJ_TYPE_NM    AS OBJ_TYPE_NM
        FROM PEN_OBJ_TYPE_M T102
        INNER JOIN PEN_OBJ_TYPE_M T103
        ON (
            T102.UP_OBJ_TYPE_ID = T103.OBJ_TYPE_ID
        )
        INNER JOIN PEN_REL_TYPE_M T101
        ON (

            	T102.OBJ_TYPE_ID = T101.REL_OBJ_TYPE_ID
	            AND T101.DOWN_REL_TYPE_NM = #{relTypeNm}
	            AND T101.OBJ_TYPE_ID = #{objTypeId}


            	T102.OBJ_TYPE_ID = T101.OBJ_TYPE_ID
	            AND T101.UP_REL_TYPE_NM = #{relTypeNm}
	            AND T101.REL_OBJ_TYPE_ID = #{objTypeId}

            AND T101.META_REL_CD = #{metaRelCd}
        )
        AND T102.DEL_YN = 'N'
        ORDER BY '['||T103.OBJ_TYPE_NM||'] '||T102.OBJ_TYPE_NM

SELECT T101.OBJ_TYPE_ID
           , T101.OBJ_ID
           , T101.OBJ_NM
           , T101.OBJ_DESC
FROM PEN_OBJ_M T101
INNER JOIN PEN_REL_TYPE_M T102
ON (
    T101.OBJ_TYPE_ID = T102.OBJ_TYPE_ID
    AND T102.REL_TYPE_ID = #{relTypeId}
    AND T102.META_REL_CD = #{metaRelCd}
)
WHERE T101.PATH_OBJ_TYPE_ID = #{objTypeId}
AND T101.PATH_OBJ_ID = #{objId}
AND T101.DEL_YN = 'N'
ORDER BY T101.OBJ_NM

SELECT T101.OBJ_TYPE_ID||T101.OBJ_ID AS ID ,T101.OBJ_ID AS OBJ_ID ,T102.OBJ_NM AS OBJ_NM ,T101.OBJ_TYPE_ID AS OBJ_TYPE_ID ,T103.OBJ_TYPE_NM AS OBJ_TYPE_NM ,T102.OBJ_DESC AS OBJ_DESC ,T101.REL_TYPE_ID AS REL_TYPE_ID ,${n_level}+1 AS LVL ,'true' AS LOADED ,'false' AS ISLEAF ,'false' AS EXPANDED ,'${path}'||'-'||ROWNUM AS PATH ,'${path}' AS PARENT_PATH FROM (
SELECT T101.REL_OBJ_ID AS OBJ_ID ,T101.REL_OBJ_TYPE_ID AS OBJ_TYPE_ID ,T101.REL_TYPE_ID AS REL_TYPE_ID FROM PEN_OBJ_R T101 INNER JOIN PEN_REL_TYPE_M T102 ON T101.OBJ_TYPE_ID = T102.OBJ_TYPE_ID AND T101.REL_OBJ_TYPE_ID = T102.REL_OBJ_TYPE_ID AND T102.META_REL_CD IN ('FT','MP','CR','DR')
AND T102.ATR_ADM_CD IN ('0','1')
#{item}
#{item}
AND T101.OBJ_REL_ANALS_CD = #{objRelAnalsDivCd}
WHERE T101.OBJ_ID = #{objId} AND T101.OBJ_TYPE_ID = #{objTypeId}
UNION ALL
SELECT T101.OBJ_ID AS OBJ_ID ,T101.OBJ_TYPE_ID AS OBJ_TYPE_ID ,T101.REL_TYPE_ID AS REL_TYPE_ID FROM PEN_OBJ_R T101 INNER JOIN PEN_REL_TYPE_M T102 ON T101.OBJ_TYPE_ID = T102.OBJ_TYPE_ID AND T101.REL_OBJ_TYPE_ID = T102.REL_OBJ_TYPE_ID AND T102.META_REL_CD IN ('FT','MP','CR','DR')
AND T102.ATR_ADM_CD IN ('0','1')
#{item}
#{item}
AND T101.OBJ_REL_ANALS_CD = #{objRelAnalsDivCd}
WHERE T101.REL_OBJ_ID = #{objId} AND T101.REL_OBJ_TYPE_ID = #{objTypeId}
) T101 INNER JOIN PEN_OBJ_M T102 ON T101.OBJ_ID = T102.OBJ_ID AND T101.OBJ_TYPE_ID = T102.OBJ_TYPE_ID AND T102.DEL_YN = 'N' INNER JOIN PEN_OBJ_TYPE_M T103 ON T101.OBJ_TYPE_ID = T103.OBJ_TYPE_ID
ORDER BY T103.SORT_NO, T102.OBJ_NM

SELECT T102.OBJ_TYPE_ID
           , T102.OBJ_ID
           , T102.OBJ_NM
