mapper.select:
SELECT OBJ_TYPE_ID AS OBJ_TYPE_ID , OBJ_ID AS OBJ_ID , DEL_YN AS DEL_YN , CRET_DT AS CRET_DT , CRETR_ID AS CRETR_ID , CHG_DT AS CHG_DT , CHGR_ID AS CHGR_ID , ADM_OBJ_ID AS ADM_OBJ_ID , OBJ_NM AS OBJ_NM , OBJ_ABBR_NM AS OBJ_ABBR_NM , OBJ_DESC AS OBJ_DESC , PATH_OBJ_TYPE_ID AS PATH_OBJ_TYPE_ID , PATH_OBJ_ID AS PATH_OBJ_ID FROM PEN_OBJ_M
OBJ_TYPE_ID = #{objTypeId}
AND OBJ_ID = #{objId}
AND ${searchKey} LIKE '%'||#{keyword}||'%'
ORDER BY OBJ_NM
