mapper.insert:

INSERT INTO O_DATAEYE_META.PEN_BBS_NTC_M(NTC_ID, NTC_NM, NTC_SBST, NTC_ST_DATE, NTC_ED_DATE, DEL_YN, CRET_DT, CRETR_ID, CHG_DT, CHGR_ID, READ_CNT, NTC_TYPE_ID
	,NTC_DSPLY, NTC_POPUP_DSPLY, NTC_SBST_TYPE
)
VALUES(#{ntc_id},#{ntc_nm},#{ntc_sbst},#{ntc_st_date},#{ntc_ed_date},'N',sysdate,#{cretr_id},sysdate,#{chgr_id},0,#{ntc_type_id}
	,#{ntc_dsply}, #{pop_dsply}, #{ntc_sbst_type}
)

mapper.update:
UPDATE O_DATAEYE_META.PEN_BBS_NTC_M
   SET    NTC_NM = #{ntc_nm}
        , NTC_SBST = #{ntc_sbst}
        , NTC_ST_DATE = #{ntc_st_date}
        , NTC_ED_DATE = #{ntc_ed_date}
        , CHG_DT = sysdate
        , CHGR_ID = #{chgr_id}
        , NTC_DSPLY = #{ntc_dsply}
        , NTC_POPUP_DSPLY = #{pop_dsply}
        , NTC_SBST_TYPE = #{ntc_sbst_type}
WHERE NTC_ID = #{ntc_id}
		AND NTC_TYPE_ID = #{ntc_type_id}


	UPDATE O_DATAEYE_META.PEN_BBS_NTC_FILE_D
	   SET   file_path_nm = #{file_path_nm}
	       , FILE_NM = #{file_nm}
	       , FILE_SYS_NM = #{file_sys_nm}
	       , FILE_SIZE = #{file_size}
	       , DOWN_CNT = 0
    WHERE  FILE_ID = #{file_id}

UPDATE O_DATAEYE_META.PEN_BBS_NTC_M
   SET    DEL_YN = 'Y'
   		, CHG_DT = sysdate
   		, CHGR_ID = #{chgr_id}
WHERE NTC_ID = #{ntc_id}
		AND NTC_TYPE_ID = #{ntc_type_id}

UPDATE O_DATAEYE_META.PEN_BBS_NTC_FILE_D
   SET    DEL_YN = 'Y'
   		, CHG_DT = sysdate
   		, CHGR_ID = #{chgr_id}
WHERE NTC_ID = #{ntc_id}
		AND NTC_TYPE_ID = #{ntc_type_id}

UPDATE O_DATAEYE_META.PEN_BBS_NTC_M SET READ_CNT = READ_CNT + 1     	   
WHERE NTC_ID = #{ntc_id}
		AND NTC_TYPE_ID = #{ntc_type_id}

mapper.select:
SELECT * FROM ( SELECT * FROM ( SELECT ROWNUM RNUM ,NTC_ID , NTC_NM , CRETR_ID , CRET_DT , CHGR_ID , CHG_DT , READ_CNT , ATTFILE_CNT FROM ( SELECT NTC_ID ,NTC_NM ,(SELECT U.USER_NM FROM O_DATAEYE_META.PEN_USER_M U WHERE A.CRETR_ID = U.USER_ID ) AS CRETR_ID ,TO_CHAR(CRET_DT,'YYYY-MM-DD HH24:MI:SS') CRET_DT ,(SELECT U.USER_NM FROM O_DATAEYE_META.PEN_USER_M U WHERE A.CHGR_ID = U.USER_ID ) AS CHGR_ID ,TO_CHAR(CHG_DT,'YYYY-MM-DD HH24:MI:SS') CHG_DT ,READ_CNT ,(SELECT COUNT(*) FROM O_DATAEYE_META.PEN_BBS_NTC_FILE_D B WHERE B.NTC_ID = A.NTC_ID) AS ATTFILE_CNT FROM O_DATAEYE_META.PEN_BBS_NTC_M A WHERE DEL_YN = 'N' AND NTC_TYPE_ID = #{ntc_type_id}
AND (A.NTC_NM LIKE '%'||UPPER(#{searchValue})||'%' OR A.NTC_SBST LIKE '%'||UPPER(#{searchValue})||'%')
AND A.NTC_NM LIKE '%'||UPPER(#{searchValue})||'%'
AND A.NTC_SBST LIKE '%'||UPPER(#{searchValue})||'%'
and A.NTC_DSPLY = 'Y'
ORDER BY CRET_DT DESC ) ) PAGETABLE WHERE RNUM <= #{endNum} ) WHERE RNUM >= #{startNum}
