mapper.update:
    	  UPDATE O_DATAEYE_META.PEN_BBS_BRD_M
			  SET CHG_DT=sysdate
			, CHGR_ID=#{chgr_id}
			, BRD_NM=#{brd_nm}
			, BRD_SBST=#{brd_sbst}
			, BRD_ST_DATE=#{brd_st_date} 
			, BRD_ED_DATE=#{brd_ed_date} 
		  WHERE BRD_TYPE_ID=#{brd_type_id} 
		    AND BRD_ID=#{brd_id}

        
        UPDATE O_DATAEYE_META.PEN_BBS_BRD_FILE_D
		   SET CHG_DT=sysdate
		     , CHGR_ID=#{chgr_id}
		     , FILE_NM=#{file_nm}
		     , FILE_SYS_NM=#{file_sys_nm}
		     , FILE_PATH_NM=#{file_path_nm}
		     , FILE_SIZE=#{file_size}
		     , DOWN_CNT=0
		 WHERE BRD_TYPE_ID=#{brd_type_id} 
		   AND BRD_ID=#{brd_id}
		   AND FILE_ID=#{file_id}

    	UPDATE O_DATAEYE_META.PEN_BBS_BRD_M    	
    	   SET DEL_YN='Y'
    	     , CHG_DT=sysdate
		     , CHGR_ID=#{chgr_id}   
    	WHERE BRD_TYPE_ID=#{brd_type_id} 
		   AND BRD_ID=#{brd_id}

    	UPDATE O_DATAEYE_META.PEN_BBS_BRD_RPL_D 
    	   SET DEL_YN='Y'
    	     , CHG_DT=sysdate
		     , CHGR_ID=#{chgr_id}    	   
    	WHERE BRD_TYPE_ID=#{brd_type_id} 
		   AND BRD_ID=#{brd_id}

    	UPDATE  O_DATAEYE_META.PEN_BBS_BRD_FILE_D    
    	    SET DEL_YN='Y'
    	      , CHG_DT=sysdate
		      , CHGR_ID=#{chgr_id} 	   
    	WHERE BRD_TYPE_ID=#{brd_type_id} 
		   AND BRD_ID=#{brd_id}

    	UPDATE O_DATAEYE_META.PEN_BBS_BRD_M SET READ_CNT = READ_CNT + 1     	   
    	WHERE BRD_TYPE_ID=#{brd_type_id} 
		   AND BRD_ID=#{brd_id}

mapper.select:
SELECT * FROM ( SELECT * FROM ( SELECT ROWNUM RNUM ,BRD_ID , BRD_NM , CRETR_ID , CRET_DT , CHGR_ID , CHG_DT , READ_CNT , REPLY_CNT , ATTFILE_CNT FROM ( SELECT A.BRD_ID ,A.BRD_NM ,(SELECT U.USER_NM FROM O_DATAEYE_META.PEN_USER_M U WHERE A.CRETR_ID = U.USER_ID ) AS CRETR_ID ,TO_CHAR(A.CRET_DT,'YYYY-MM-DD HH24:MI:SS') CRET_DT ,(SELECT U.USER_NM FROM O_DATAEYE_META.PEN_USER_M U WHERE A.CHGR_ID = U.USER_ID ) AS CHGR_ID ,TO_CHAR(A.CHG_DT,'YYYY-MM-DD HH24:MI:SS') CHG_DT ,A.READ_CNT ,(SELECT COUNT(*) FROM O_DATAEYE_META.PEN_BBS_BRD_RPL_D WHERE BRD_ID = A.BRD_ID AND BRD_TYPE_ID = A.BRD_TYPE_ID) AS REPLY_CNT ,(SELECT COUNT(*) FROM O_DATAEYE_META.PEN_BBS_BRD_FILE_D WHERE BRD_ID = A.BRD_ID AND BRD_TYPE_ID = A.BRD_TYPE_ID) AS ATTFILE_CNT FROM O_DATAEYE_META.PEN_BBS_BRD_M A WHERE A.DEL_YN = 'N' AND A.BRD_TYPE_ID = #{brd_type_id}
AND (A.BRD_NM LIKE '%'||UPPER(#{searchValue})||'%' OR A.BRD_SBST LIKE '%'||UPPER(#{searchValue})||'%')
AND A.BRD_NM LIKE '%'||UPPER(#{searchValue})||'%'
AND A.BRD_SBST LIKE '%'||UPPER(#{searchValue})||'%'
ORDER BY A.CRET_DT DESC ) ) PAGETABLE WHERE RNUM <= #{endNum} ) WHERE RNUM >= #{startNum}
