mapper.select:
SELECT T101.OBJ_TYPE_ID , T101.OBJ_ID , T101.ADM_OBJ_ID , T101.OBJ_NM , T101.OBJ_DESC , T104.OBJ_TYPE_ID AS APPSYS_TYPE_ID, T104.OBJ_ID AS APPSYS_ID, T104.OBJ_NM AS APPSYS_NM, MAX(CASE WHEN T102.ATR_ID_SEQ = 101 THEN T103.CD_NM END) AS DBTYPE , MAX(CASE WHEN T102.ATR_ID_SEQ = 102 THEN T102.OBJ_ATR_VAL END) AS DB_VER, MAX(CASE WHEN T102.ATR_ID_SEQ = 103 THEN T103.CD_NM END) AS DB_USAGE, MAX(CASE WHEN T102.ATR_ID_SEQ = 105 THEN T102.OBJ_ATR_VAL END) AS DB_IP, MAX(CASE WHEN T102.ATR_ID_SEQ = 106 THEN T102.OBJ_ATR_VAL END) AS DB_PORT, MAX(CASE WHEN T102.ATR_ID_SEQ = 108 THEN T102.OBJ_ATR_VAL END) AS DB_CHARSET, MAX(CASE WHEN T102.ATR_ID_SEQ = 110 THEN T102.OBJ_ATR_VAL END) AS DB_MGR FROM O_DATAEYE_META.PEN_OBJ_M T101 LEFT OUTER JOIN O_DATAEYE_META.PEN_OBJ_D T102 ON ( T102.OBJ_TYPE_ID = T101.OBJ_TYPE_ID AND T102.OBJ_ID = T101.OBJ_ID ) LEFT OUTER JOIN O_DATAEYE_META.PEN_OBJ_M T104 ON ( T101.PATH_OBJ_TYPE_ID = T104.OBJ_TYPE_ID AND T101.PATH_OBJ_ID = T104.OBJ_ID ) LEFT OUTER JOIN O_DATAEYE_META.PEN_CD_M T103 ON ( ( T103.CD_GRP_ID = 'SYS_DBTYPE' AND T102.ATR_ID_SEQ = 101 AND T103.CD_ID = T102.OBJ_ATR_VAL ) OR ( T103.CD_GRP_ID = 'CM_SYS_CONNTYPE' AND T102.ATR_ID_SEQ = 103 AND T103.CD_ID = T102.OBJ_ATR_VAL ) ) WHERE T101.DEL_YN = 'N' AND T101.OBJ_TYPE_ID = #{objTypeId}
AND T101.OBJ_NM LIKE '%'||#{searchValue}||'%'
AND T101.PATH_OBJ_TYPE_ID = #{pathObjTypeId}
AND T101.PATH_OBJ_ID = #{pathObjId}
GROUP BY T101.OBJ_TYPE_ID , T101.OBJ_ID , T101.ADM_OBJ_ID , T101.OBJ_NM , T101.OBJ_DESC, T104.OBJ_TYPE_ID, T104.OBJ_ID, T104.OBJ_NM ORDER BY T101.OBJ_NM

SELECT T101.OBJ_TYPE_ID , T101.OBJ_ID , T101.ADM_OBJ_ID , T101.OBJ_NM , T101.OBJ_DESC , T104.OBJ_TYPE_ID AS DBINST_TYPE_ID, T104.OBJ_ID AS DBINST_ID, T104.OBJ_NM AS DBINST_NM, MAX(CASE WHEN T102.ATR_ID_SEQ = 101 THEN T103.OBJ_TYPE_ID END) AS JDBC_TYPE_ID, MAX(CASE WHEN T102.ATR_ID_SEQ = 101 THEN T103.OBJ_ID END) AS JDBC_ID, MAX(CASE WHEN T102.ATR_ID_SEQ = 101 THEN T103.OBJ_NM END) AS JDBC_NM FROM O_DATAEYE_META.PEN_OBJ_M T101 LEFT OUTER JOIN O_DATAEYE_META.PEN_OBJ_D T102 ON ( T102.OBJ_TYPE_ID = T101.OBJ_TYPE_ID AND T102.OBJ_ID = T101.OBJ_ID ) LEFT OUTER JOIN O_DATAEYE_META.PEN_OBJ_M T104 ON ( T101.PATH_OBJ_TYPE_ID = T104.OBJ_TYPE_ID AND T101.PATH_OBJ_ID = T104.OBJ_ID ) LEFT OUTER JOIN O_DATAEYE_META.PEN_OBJ_M T103 ON ( T102.OBJ_ATR_VAL = T103.OBJ_ID AND T102.ATR_ID_SEQ = 101 ) WHERE T101.DEL_YN = 'N' AND T101.OBJ_TYPE_ID = #{objTypeId}
AND T101.OBJ_NM LIKE '%'||#{searchValue}||'%'
GROUP BY T101.OBJ_TYPE_ID , T101.OBJ_ID , T101.ADM_OBJ_ID , T101.OBJ_NM , T101.OBJ_DESC, T104.OBJ_TYPE_ID , T104.OBJ_ID , T104.OBJ_NM ORDER BY T101.OBJ_NM

SELECT T101.OBJ_TYPE_ID , T101.OBJ_ID , T101.ADM_OBJ_ID , T101.OBJ_NM , T101.OBJ_DESC , MAX(CASE WHEN T102.ATR_ID_SEQ = 101 THEN T103.CD_NM END) AS DBTYPE , MAX(CASE WHEN T102.ATR_ID_SEQ = 102 THEN T103.CD_NM END) AS JDBC_DRIVER, MAX(CASE WHEN T102.ATR_ID_SEQ = 103 THEN T102.OBJ_ATR_VAL END) AS JDBC_URL FROM O_DATAEYE_META.PEN_OBJ_M T101 LEFT OUTER JOIN O_DATAEYE_META.PEN_OBJ_D T102 ON ( T102.OBJ_TYPE_ID = T101.OBJ_TYPE_ID AND T102.OBJ_ID = T101.OBJ_ID ) LEFT OUTER JOIN O_DATAEYE_META.PEN_CD_M T103 ON ( ( T103.CD_GRP_ID = 'SYS_DBTYPE' AND T102.ATR_ID_SEQ = 101 AND T103.CD_ID = T102.OBJ_ATR_VAL ) OR ( T103.CD_GRP_ID = 'SYS_JDBC_DRIVER' AND T102.ATR_ID_SEQ = 102 AND T103.CD_ID = T102.OBJ_ATR_VAL ) ) WHERE T101.DEL_YN = 'N' AND T101.OBJ_TYPE_ID = #{objTypeId}
AND T101.OBJ_NM LIKE '%'||#{searchValue}||'%'
GROUP BY T101.OBJ_TYPE_ID , T101.OBJ_ID , T101.ADM_OBJ_ID , T101.OBJ_NM , T101.OBJ_DESC ORDER BY T101.OBJ_NM
