mapper.select:
SELECT F1.MENU_REQ_UUID AS MENU_REQ_UUID , F1.CRET_DT AS CRET_DT , F1.CRETR_ID AS CRETR_ID , F1.APP_ID AS APP_ID , NVL((SELECT DISTINCT APP_NM FROM O_DATAEYE_META.PEN_APP_M WHERE APP_ID =TRIM(F1.APP_ID) ), '-') AS APP_NM , F1.USER_ID AS USER_ID , NVL((SELECT DISTINCT USER_NM FROM O_DATAEYE_META.PEN_USER_M WHERE USER_ID =TRIM(F1.USER_ID) ), '-') AS USER_NM , NVL((SELECT DISTINCT ORG_ID FROM O_DATAEYE_META.PEN_USER_M WHERE USER_ID =TRIM(F1.USER_ID) ), '-') AS ORG_ID , F1.SESS_ID AS SESS_ID , F1.USER_IP AS USER_IP , F1.SRV_IP AS SRV_IP , F1.SRV_PORT AS SRV_PORT , CASE WHEN SUBSTR(F1.MENU_ID,1,9)='undefined' THEN '-' ELSE F1.MENU_ID END AS MENU_ID , CASE WHEN SUBSTR(F1.MENU_NM,1,9)='undefined' THEN '-' ELSE F1.MENU_NM END AS MENU_NM , F1.URL AS URL , F1.VIEW_ID AS VIEW_ID , F1.QUERY_STR AS QUERY_STR , F1.REQ_DT AS REQ_DT , F1.REFE_INFO AS REFE_INFO , CASE WHEN F1.HTTP_STATUS='200' THEN '정상' ELSE TO_CHAR(F1.HTTP_STATUS) END AS HTTP_STATUS FROM O_DATAEYE_META.PEN_SYS_MENU_L F1
AND TO_CHAR(F1.REQ_DT, 'yyyy-mm-dd') = #{fromData}
ORDER BY F1.REQ_DT DESC
