mapper.select:
SELECT COUNT(1)
FROM O_DATAEYE_META.PEN_USER_M T101
WHERE T101.USER_ID = #{userId}

        SELECT 
            T101.*
        FROM O_DATAEYE_META.PEN_SYS_USER_GRP_M T101
        
            
                AND T101.USER_GRP_ID = #{userGrpId}
            
            
                AND T101.USER_GRP_TYPE = #{userGrpType}
            
            
                AND T101.USER_GRP_TYPE != #{notUserGrpType}
            
            
                
				    AND LOWER(T101.USER_GRP_ID) LIKE  LOWER('%'||#{searchValue}||'%')
    			
    			
    				AND LOWER(T101.USER_GRP_NM) LIKE  LOWER('%'||#{searchValue}||'%')
    			
             
        
        ORDER BY T101.USER_GRP_NM

mapper.delete:
		{call
        	declare
        	begin
        		DELETE FROM O_DATAEYE_META.PEN_SYS_USER_GRP_M WHERE USER_GRP_ID = #{keyVal};
            	DELETE FROM O_DATAEYE_META.PEN_SYS_GRP_USER_R where USER_GRP_ID = #{keyVal};
        	end
    	}

		{call
        	declare
        	begin
        		DELETE FROM O_DATAEYE_META.PEN_SYS_USER_GRP_M WHERE USER_GRP_ID = #{keyVal};
            	DELETE FROM O_DATAEYE_META.PEN_SYS_GRP_R where USER_GRP_ID = #{keyVal};
        	end
    	}

DELETE FROM O_DATAEYE_META.PEN_SYS_GRP_R
WHERE USER_GRP_ID = #{keyVal}
AND REL_USER_GRP_ID IN

	#{item.USER_GRP_ID}

     		DELETE FROM O_DATAEYE_META.PEN_SYS_PRIV_MENU_R 
     		WHERE MENU_ID=#{menuId} 
     		   AND PRIV_RCVR_GBN=#{privRcvrGbn}
