            		#{item}

mapper.select:
SELECT COUNT(1)
FROM O_DATAEYE_META.PEN_SYS_GRP_R T101

    
        AND T101.USER_GRP_ID = #{userGrpId}
    
    
        AND T101.REL_USER_GRP_ID = #{relUserGrpId}

SELECT COUNT(1)
FROM O_DATAEYE_META.PEN_SYS_GRP_USER_R T101

    
        AND T101.USER_GRP_ID = #{userGrpId}

SELECT COUNT(1) 
FROM O_DATAEYE_META.PEN_SYS_GRP_USER_R
WHERE USER_ID = #{userId} 
AND USER_GRP_ID = #{userGrpId}

mapper.delete:

	DELETE 
	FROM O_DATAEYE_META.PEN_SYS_PRIV_OBJ_R
	WHERE OBJ_TYPE_ID = #{OBJ_TYPE_ID}
	  AND OBJ_ID = #{OBJ_ID}
	  AND PRIV_RCVR_ID = #{PRIV_RCVR_ID}
