mapper.select:
SELECT SABUN , USER_ID , USER_NM , BUSEO , BUSE_CODE , DEPT , AD_ID , EMAIL_ADDR , HP_NO , LOGIN_FAIL_CNT , LOGIN_LAST_DT FROM (
SELECT USER_ID AS SABUN , USER_ID , USER_NM , ORG_ID AS BUSEO , ORG_ID AS BUSE_CODE , ORG_ID AS DEPT , TEL_NO AD_ID , EMAIL_ADDR , HP_NO , LOGIN_FAIL_CNT , LOGIN_LAST_DT FROM O_DATAEYE_META.PEN_USER_M -- WHERE USER_ID NOT IN (SELECT SABUN FROM O_META.TB_USER)
) F1 WHERE F1.AD_ID = #{userId}

mapper.update:
UPDATE O_DATAEYE_META.PEN_USER_M T101
SET T101.LOGIN_FAIL_CNT = T101.LOGIN_FAIL_CNT+1
WHERE T101.USER_ID = #{userId}

UPDATE O_DATAEYE_META.PEN_USER_M T101
SET T101.ACCOUNT_NON_EXPIRED = 'N'
WHERE T101.USER_ID = #{userId}

UPDATE O_DATAEYE_META.PEN_USER_M T101
SET T101.ACCOUNT_NON_LOCKED = 'N'
WHERE T101.USER_ID = #{userId}

UPDATE O_DATAEYE_META.PEN_USER_M T101
SET T101.CREDENTIALS_NON_EXPIRED = 'N'
WHERE T101.USER_ID = #{userId}

UPDATE O_DATAEYE_META.PEN_USER_M T101
SET T101.ACCOUNT_ENABLED = 'N'
WHERE T101.USER_ID = #{userId}
